import React from 'react'
import './AddOrEditOrder.css'
import * as actions from '../../store/actions/OrderCreator'
import { connect } from 'react-redux'

class AddOrEditOrder extends React.Component{

    state = {
        name:'',
        email:'',
        product:'',
        qty:'',
    }

    handleNameChange = (event) => {
        this.setState({
            name:event.target.value
        })
    }

    handleEmailChange = (event) => {
        this.setState({
            email:event.target.value
        })
    }

    handleProductChange = (event) => {
        this.setState({
            product:event.target.value
        })
    }

    handleQtyChange = (event) => {
        this.setState({
            qty:event.target.value
        })
    }

    toggleAddEditOrder = () => {

    }

    render(){

        let editedOrder = 
        {
            name:'',
            email:'',
            product:'',
            qty:''
        }

        if(this.props.editedOrder!==null)
            editedOrder = this.props.editedOrder

        return (
            <div className="Addoreditorder">
                <h1>Add or Edit Order</h1>
                <p>Fill The Order</p>
                <form>
                    <label>Name</label>
                    <input type="text" value={editedOrder.customer_name} onChange={(event)=>{this.handleNameChange(event)}} />
                    <label>Email</label>
                    <input type="text" value={editedOrder.customer_email} onChange={(event)=>{this.handleEmailChange(event)}} />
                    <label>Product</label>
                    <input type="text" value={editedOrder.product} onChange={(event)=>{this.handleProductChange(event)}} />
                    <label>Qty</label>
                    <input type="number" value={editedOrder.quantity} onChange={(event)=>{this.handleQtyChange(event)}} />
                    <button onClick={this.toggleAddEditOrder}>Toggle to Add Product</button>
                    <input type="submit" value={this.props.editing?"Add":"Edit"} />
                    
                </form>
            </div>
        )
    }
}

const mapStateToProps = state => {
    return {
        editedOrder: state.editedOrder,
        editing: state.editing
    }
}

const mapDispatchToProps = dispatch => {
    return {
        toggleEditing: ()=>dispatch(actions.toggleEditing())
    }
}

export default connect(mapStateToProps,mapDispatchToProps)(AddOrEditOrder)
