import * as actionTypes from './action'
import axios from 'axios'

export const fetchOrders = (orders) => {
    return {
        type: actionTypes.FETCH_ORDERS,
        orders: orders
    }
}

export const getOrder = (id) => {
    return{
        type:actionTypes.GET_ORDER,
        id:id
    }
}

export const fetchingOrders = (count) => {
    return dispatch => {
        return axios.get(`https://utilizeapplication-b8e0e.firebaseio.com/orders.json?orderBy="id"&startAt=${count}&endAt=${count+30}`)
            .then(res=>dispatch(fetchOrders(res.data)))
            .catch(error=>console.log(error))
    }
}

export const toggleEditing = () => {
    return {
        type:actionTypes.TOGGLE_EDITING,        
    }
}

export const clearEditingOrder = () => {
    return {
        type:actionTypes.CLEAR_EDITING_ORDER
    }
}

