import * as actions from '../actions/action'
import order from '../../components/Orders/Order/Order'
const editedOrder = {
    customer_name:'',
    customer_email:'',
    product:'',
    quantity:'',
    id:''
}


const initialState = {
    orders: [],
    editedOrder:{...editedOrder},
    editing:false
}

const reducer = (state= initialState, action) => {
    let orders = null
    let editedOrder = null
    switch(action.type){
        case actions.FETCH_ORDERS:
            return {
                ...state,
                orders:action.orders
            }
        case actions.GET_ORDER:
            console.log(action.id)
            editedOrder = state.orders.find(id => {console.log(state.orders[id])})
            console.log(editedOrder)
            return{
                ...state,
                editedOrder:editedOrder
            }
        case actions.TOGGLE_EDITING:
            const toggleEditing = !{...state}["editing"]
            return{
                ...state,
                editing: toggleEditing
            }
        case actions.CLEAR_EDITING_ORDER:
            return{
                ...state,
                editedOrder: {...editedOrder}
            }
        case actions.ADD_TO_ORDERS:
            orders = [...state.orders]
            console.log(action.order)
            return{
                ...state,
                orders
            }
        case actions.DELETE_ORDER:
            orders = [...state.orders]
            orders.splice(action.id,1)
            return{
                ...state,
                orders
            }
        // case actions.HANDLE_INPUT_CHANGE:
        //     return {
        //         ...state,
        //         editedOrder:{
        //             ...state.editedOrder,
        //             [action.input]:action.value
        //         }
        //     }
    }

    return state
}

export default reducer
