import React from 'react'
import './AddOrEditOrder.css'
import { connect } from 'react-redux'

class AddOrEditOrder extends React.Component{

    state = {
        name:'',
        email:'',
        product:'',
        qty:'',
    }

    handleNameChange = (event) => {
        this.setState({
            name:event.target.value
        })
    }

    handleEmailChange = (event) => {
        this.setState({
            email:event.target.value
        })
    }

    handleProductChange = (event) => {
        this.setState({
            product:event.target.value
        })
    }

    handleQtyChange = (event) => {
        this.setState({
            qty:event.target.value
        })
    }

    render(){
        return (
            <div className="Addoreditorder">
                <h1>Add or Edit Order</h1>
                <p>Fill The Order</p>
                <form>
                    <label>Name</label>
                    <input type="text" value={this.props.editedProduct.name} onChange={(event)=>{this.handleNameChange(event)}} />
                    <label>Email</label>
                    <input type="text" value={this.props.editedProduct.email} onChange={(event)=>{this.handleEmailChange(event)}} />
                    <label>Product</label>
                    <input type="text" value={this.props.editedProduct.product} onChange={(event)=>{this.handleProductChange(event)}} />
                    <label>Qty</label>
                    <input type="number" value={this.props.editedProduct.qty} onChange={(event)=>{this.handleQtyChange(event)}} />
                    <input type="submit" value="Add Product" />
                    
                </form>
            </div>
        )
    }
}

const mapStateToProps = state => {
    return {
        editedProduct: state.editedProduct
    }
}

export default connect()(AddOrEditOrder)
